import Canvas from './Canvas';
import { Link } from 'react-router-dom';
import logo from '../images/homepage/header8.png';
import info from '../images/homepage/info.png';

import {db} from './Firebase.js'
import { collection, addDoc, getDoc, getDocs } from "firebase/firestore";


const useFirestore = (collection) => {

  const [docs, setDocs] = useState([]);

  useEffect (() => {
      //new collection reference
      const newcoll = (col(projectFirestore, collection))
          const q = query(newcoll, orderBy('prueba', 'qwe'));
          const unsub = onSnapshot(q, (snapshot) => {
              let documents = [];
              snapshot.forEach(doc => {
                  documents.push({...doc.data(), id: doc.id})
              })
              setDocs(documents);
          });

      return () => newcoll();
  },[collection]);
}



function Home() {
  return( 
    <div className="container">
      <div className="header-collection">
        <div className="header-logo">
          <a href="/">
            <img src={logo} width="1000" alt="" />
          </a>
        </div>
      </div>

     <div className="nav-collection">
       <div className="nav-item">
        <img src={info} width="300" alt="" />
       </div>
       
      </div>
      <div className="ad">
        <div className="ad-collection">
          <Canvas />
        </div>
      </div>
    </div>
  );
}

export default Home;
